func:
var power = flow.get("powerstate");
if (power=="on") msg.payload=Boolean(true);
    else msg.payload=Boolean(false);
msg.test=power;
return msg;

if (msg.payload=="OS1")   msg.background="green",
                            msg.label="<font color=white>MODE: OPERATE",
                            flow.set("mode", "^OS0;");
    else if (msg.payload=="OS0")  msg.background="yellow",
                                    msg.label="<font color=black>MODE: STANDBY",
                                    flow.set("mode", "^OS1;");
        else    msg.background="gray",
                msg.label="<font color=black>UNK STATE";
return msg;

if (msg.payload=="KPA1500") msg.background="green",
                        msg.label="<font color=white>POWER: ON",
                        flow.set("powerbutton", "^on0;"),
                        flow.set("powerstate", "on");
    else if (msg.payload=="timeout") msg.background="yellow",
         msg.label="<font color=black>POWER: OFF",
         flow.set("powerbutton", "^on1;"),
         flow.set("powerstate", "off");
         else   msg.background="gray",
                msg.label="<font color=black>UNK STATE",
                flow.set("powerstate", "off");
msg.payload="dummy";
return msg;

var message = msg.payload;

if (message.includes("MODE_STBY"))   

                            msg.topic="forwardPowerGate",
                            msg.payload="open",
                            node.send(msg),
                            msg.topic="inputPowerGate",
                            msg.payload="close",
                            node.send(msg);

    else if (message.includes("PA OPER"))

                            msg.topic="forwardPowerGate",
                            msg.payload="close",
                            node.send(msg),
                            msg.topic="inputPowerGate",
                            msg.payload="open",
                            node.send(msg);

return;

var message = msg.payload;

if (message.includes("TR_STATE_RX") || message.includes("MODE_STBY"))   

        msg.background="#097479",
        msg.label="<font color=white>RX";

    else if (message.includes("TR_STATE_TX"))

            msg.background="green",
            msg.label="<font color=white>TX";

return(msg);

if (msg.payload=="AMI")   msg.background="green",
                            msg.label="<font color=white>ATU: INLINE",
                            flow.set("atuMode", "^AMB;");
    else if (msg.payload=="AMB")  msg.background="#097479",
                                    msg.label="<font color=white>ATU: BYPASS",
                                    flow.set("atuMode", "^AMI;");
        else    msg.background="gray",
                msg.label="<font color=black>UNK STATE";
return msg;

atuMode = flow.get("atuMode");

if (atuMode=="^AMI;")   msg.background="gray",
                        msg.label="",
                        flow.set("atu", "");

    else if (atuMode=="^AMB;" & msg.payload=="TP0") msg.background="green",
                                                    msg.label="<font color=white>TUNE",
                                                    flow.set("atu", "^FT;");

        else if (atuMode=="^AMB;" & msg.payload=="TP1") msg.background="yellow",
                                                        msg.label="<font color=black>TUNING",
                                                        flow.set("atu", "^FE;");

return msg;

message = msg.payload;

if (message.includes("FAULT"))  msg.background = "red",
                                msg.font = "white";

    else if (message.includes("OVR"))   msg.background = "yellow",
                                        msg.font = "black";

        else    msg.background = "transparent",
                msg.font = "white";

return msg;
